mapper.select:
select * from staff where working > 0
and staffID = #{staffId}
and staffName = #{staffName}
and staffSex = #{staffSex}
and staffAge = #{staffAge}
and staffEducation = #{staffEducation}
and staffPosition = #{staffPosition}
and staffTitle = #{staffTitle}
and staffAddress = #{staffAddress}
and staffTel = #{staffTel}
and staffSalaryLevel = #{staffSalaryLevel}
and staffDepartment = #{staffDepartment}
